import React from "react";
import "./App.css";
import axios from "axios";

class App extends React.Component {
  state = {
    todoList: [],
    activeItem: {
      id: null,
      title: "",
      completed: false,
    },
    editing: false,
  };

  // Tak ma wyglądać funkcja, żeby nie trzeba było bindować
  fetchTask = () => {
    axios.get("http://127.0.0.1:8000/api/task-list").then((res) => {
      console.log(res.data);
      this.setState({
        todoList: res.data,
      });
    });
  };

  // cookie skopiowane z dokumentacji django csrf. Mogą pojawiać się erroro
  // kwestia zabezpieczeń
  getCookie = (name) => {
    var cookieValue = null;
    if (document.cookie && document.cookie !== "") {
      var cookies = document.cookie.split(";");
      for (var i = 0; i < cookies.length; i++) {
        var cookie = cookies[i].trim();
        // Does this cookie string begin with the name we want?
        if (cookie.substring(0, name.length + 1) === name + "=") {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  };

  // żeby odpalić funkcję fetch task, trzeba dać this.fetchTask().
  // inaczej nie zadziała, mimo, że nie przyjmuje argumentów to należy użyć ()
  componentDidMount() {
    this.fetchTask();
  }

  handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value;
    console.log("Name", name);
    console.log("Value", value);

    this.setState({
      activeItem: {
        ...this.state.activeItem,
        title: value,
      },
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    console.log("Item: ", this.state.activeItem);

    const url = "http://127.0.0.1:8000/api/task-create/";
    // axios.post(url, { headers: { "Content-Type": "application/json" } });
    axios
      .post(url, this.state.activeItem) //postuje słownika activeItem do backendu
      .then((res) => {
        console.log(res);
        this.fetchTask(); //pobiera listę
        this.setState({
          // resetuje stan activeItem
          activeItem: {
            id: null,
            title: "",
            completed: false,
          },
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  render() {
    let tasks = this.state.todoList;
    return (
      <div className="container">
        <div id="task-container">
          <div id="form-wrapper">
            <form id="form" onSubmit={this.handleSubmit}>
              <div className="flex-wrapper">
                <div style={{ flex: 6 }}>
                  {/* The flex-grow property specifies how much 
                the item will grow relative to the rest of the flexible items inside the same container. */}
                  <input
                    onChange={this.handleChange}
                    placeholder="Add task"
                    className="form-control"
                    id="title"
                  />
                </div>
                <div style={{ flex: 1 }}>
                  <input
                    id="submit"
                    type="submit"
                    name="add"
                    className="btn btn-warning"
                    value="Submit"
                  />
                </div>
              </div>
            </form>
          </div>
          <div id="list-wrapper">
            {tasks.map((task, index) => (
              <div key={index} className="task-wrapper flex-wrapper">
                <div style={{ flex: 7 }}>
                  {/* tutaj flex 7 a potem oznacza, że ten będzie 7x większy od 1, 14 2 2 zrobi to samo */}
                  <span>{task.title}</span>
                </div>
                <div style={{ flex: 1 }}>
                  <button className="btn btn-sm btn-outline-info">Edit</button>
                </div>
                <div style={{ flex: 1 }}>
                  <button className="btn btn-sm btn-outline-dark delete">
                    -
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
