import React from "react";
import "./App.css";
import axios from "axios";

class App extends React.Component {
  state = {
    todoList: [],
    activeItem: {
      id: null,
      title: "",
      completed: false,
    },
    editing: false,
  };

  // Tak ma wyglądać funkcja, żeby nie trzeba było bindować
  fetchTask = () => {
    axios.get("http://127.0.0.1:8000/api/task-list").then((res) => {
      console.log(res.data);
      this.setState({
        todoList: res.data,
      });
    });
  };

  // żeby odpalić funkcję fetch task, trzeba dać this.fetchTask().
  // inaczej nie zadziała, mimo, że nie przyjmuje argumentów to należy użyć ()
  componentWillMount() {
    this.fetchTask();
  }

  render() {
    let tasks = this.state.todoList;
    return (
      <div className="container">
        <div id="task-container">
          <div id="form-wrapper">
            <form id="form">
              <div className="flex-wrapper">
                <div style={{ flex: 6 }}>
                  {/* The flex-grow property specifies how much 
                the item will grow relative to the rest of the flexible items inside the same container. */}
                  <input
                    placeholder="Add task"
                    className="form-control"
                    id="title"
                  />
                </div>
                <div style={{ flex: 1 }}>
                  <input
                    id="submit"
                    type="submit"
                    name="add"
                    className="btn btn-warning"
                    value="Submit"
                  />
                </div>
              </div>
            </form>
          </div>
          <div id="list-wrapper">
            {tasks.map((task, index) => (
              <div key={index} className="text-wrapper flex-wrapper">
                <span>{task.title}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
